SELECT tasks.id AS id FROM tasks LEFT JOIN tasks_cstm tasks_cstm ON tasks.id = tasks_cstm.id_c WHERE tasks.date_modified >= DATE_ADD(tasks., INTERVAL   ) AND (tasks.status = 'InProgress' OR tasks.status = 'FollowUp') AND tasks_cstm.nbfollowup_c = '3' AND NOT EXISTS (SELECT * FROM aow_processed WHERE aow_processed.aow_workflow_id='dac6a086-aeb1-4497-5e4d-5bf6b2811b97' AND aow_processed.parent_id=tasks.id AND aow_processed.status = 'Complete' AND aow_processed.deleted = 0) AND tasks.deleted = 0;